import React, { useState } from "react";

export default function App() {
  const [salesData, setSalesData] = useState("");
  const [recommendation, setRecommendation] = useState(null);

  const handleInputChange = (e) => {
    setSalesData(e.target.value);
  };

  const handleAnalyze = () => {
    const parsedData = parseInt(salesData);
    if (!isNaN(parsedData)) {
      const recommendedStock = Math.ceil(parsedData * 1.1); // +10% buffer
      const recommendedStaff = Math.max(1, Math.floor(parsedData / 50));
      setRecommendation({ stock: recommendedStock, staff: recommendedStaff });
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "2rem auto", fontFamily: "Arial" }}>
      <div style={{ border: "1px solid #ccc", padding: "2rem", borderRadius: "8px" }}>
        <h1 style={{ marginBottom: "1rem" }}>Optimiseur de Commandes</h1>
        <input
          type="number"
          placeholder="Ventes de la semaine dernière"
          value={salesData}
          onChange={handleInputChange}
          style={{ padding: "0.5rem", width: "100%", marginBottom: "1rem" }}
        />
        <button onClick={handleAnalyze} style={{ padding: "0.5rem 1rem" }}>
          Analyser
        </button>
        {recommendation && (
          <div style={{ marginTop: "1rem" }}>
            <p>Stock recommandé : <strong>{recommendation.stock}</strong></p>
            <p>Personnel recommandé : <strong>{recommendation.staff}</strong></p>
          </div>
        )}
      </div>
    </div>
  );
}
