import React, { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";

export default function StockOptimizerApp() {
  const [salesData, setSalesData] = useState("");
  const [recommendation, setRecommendation] = useState(null);

  const handleInputChange = (e) => {
    setSalesData(e.target.value);
  };

  const handleAnalyze = () => {
    // Simulation simple de recommandation
    const parsedData = parseInt(salesData);
    if (!isNaN(parsedData)) {
      const recommendedStock = Math.ceil(parsedData * 1.1); // +10% pour buffer
      const recommendedStaff = Math.max(1, Math.floor(parsedData / 50));
      setRecommendation({ stock: recommendedStock, staff: recommendedStaff });
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 space-y-6">
      <Card>
        <CardContent className="p-6 space-y-4">
          <h1 className="text-xl font-bold">Optimiseur de Commandes</h1>
          <Input
            type="number"
            placeholder="Ventes de la semaine dernière"
            value={salesData}
            onChange={handleInputChange}
          />
          <Button onClick={handleAnalyze}>Analyser</Button>
          {recommendation && (
            <div className="mt-4 text-sm text-gray-700">
              <p>Stock recommandé : <strong>{recommendation.stock}</strong></p>
              <p>Personnel recommandé : <strong>{recommendation.staff}</strong></p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}
